static BitmapID get_me_a_bitmap(Painter *p_painter,unsigned char *p_array) {
	
	int w=p_array[0];
	int h=p_array[1];
	
	BitmapID b = p_painter->create_bitmap( Size(w,h) );
	
	for (int i=0;i<(w*h);i++) {
		
		unsigned char v=p_array[2+i];
		
		Color c;
		if (v==255)
			c=Color(255,0,255);
		else
			c=Color(v);
		
		int x=i%w;
		int y=i/w;
		
		p_painter->set_bitmap_pixel( b, Point(x,y), c );
		
	}
	
	return b;
}



